%cassandra

CREATE KEYSPACE IF NOT EXISTS ks_bds 
WITH replication = {
	'class' : 'SimpleStrategy', //in production use NetworkTopologyStrategy
	'replication_factor' : 1
};


//describe keyspaces;

describe keyspace ks_bds;

%cassandra

use ks_bds;

DROP TABLE IF EXISTS raw_data;

CREATE TABLE IF NOT EXISTS raw_data ( 
	machine text,
	status text,
	timestamp timestamp,
	temperature double,
	pressure double,
	humidity double,
	PRIMARY KEY (machine,timestamp) 
);

//in production we should use bucketing (week,month,year, etc)


DROP TABLE IF EXISTS avg_temp_machine;

CREATE TABLE IF NOT EXISTS avg_temp_machine ( 
	machine text,
    timestamp timestamp,
    avg_temp double,
	PRIMARY KEY (machine,timestamp)
);

describe table raw_data;

