import org.apache.spark.sql.ForeachWriter
import org.apache.spark.sql.Row
import org.apache.spark.SparkConf

import com.datastax.spark.connector._
import com.datastax.spark.connector.cql.CassandraConnector

//better to use  https://spark.apache.org/docs/latest/api/scala/index.html#org.apache.spark.sql.sources.StreamSinkProvider but rightnow is EXPERIMENTAL
val writer = new ForeachWriter[Row] {
    
    private def cqlData(record: Row): String = s"""
       insert into ks_bds.raw_data (machine, status, timestamp, temperature, pressure, humidity)
       values('${record(0)}', '${record(1)}', '${record(2)}', ${record(3)},${record(4)},${record(5)})"""

      
    override def open(partitionId: Long, version: Long) = {
        true
      }
      
    def process(record: Row) = {
       // println(s"Saving record: $record")
        val sparkConf = new SparkConf().set("spark.cassandra.connection.host", "192.168.99.100") //in real code use a Object/Singleton, Zeppeling bug here?¿ as using 'spark' triggers a null pointer exception
        CassandraConnector(sparkConf).withSessionDo(session =>
        session.execute(cqlData(record))
    )
  }
      
      override def close(errorOrNull: Throwable) = {
    //connection.keep_alive_ms	--> 5000ms :	Period of time to keep unused connections open
      }
    }

